$(document).on('click','a',function(e){e.preventDefault();var section=$(this).attr('href');if($('section#'+section).length){app.sections.toggle(section)}else{app.get({controller:section,action:'view'})}}).on('submit','form',function(e,i){e.preventDefault();app.post({controller:$(this).attr('controller'),action:$(this).attr('action'),data:app.toJSONString(e.currentTarget)})}).on('change','select',function(){var selclass='valid';if($(this).val()!="")
$(this).addClass(selclass);else $(this).removeClass(selclass)})