class Date {

	constructor(){
		this.current = this.actual()

	}
	actual(){
        var arg = arg||null, 
		fecha_actual = new Date(), 
		mes = fecha_actual.getMonth()+1, 
		dia = fecha_actual.getDate()
	
        if(dia<=9)dia="0"+dia
        if(mes<=9)mes="0"+mes
        return  arg=="y"?fecha_actual.getFullYear()
				:arg=="m"?mes
				:arg=="d"?fecha_actual.getDate()
				:fecha_actual.getFullYear() +"-"+ mes +"-"+ dia
	}
	format (formatOut, strDate){ 

        var fecha = !isEmpty(strDate)?strDate.toString():this.actual();
    
        if (fecha.indexOf("/")>0){
            var mdy = fecha.split('/');
            var d = ("0" + mdy[0]).slice (-2);
            var m = ("0" + mdy[1]).slice (-2);
            var a = mdy[2];
        }else if(fecha.indexOf("-")>0){
            var mdy = fecha.split('-');
            var d = ("0" + mdy[2]).slice (-2);
            var m = ("0" + mdy[1]).slice (-2);
            var a = mdy[0];
        }else if(fecha.length==4){
            var d = fecha.substr(2);
            var m = fecha.substr(0,2);
            var a =  fechaActual('y');
        }else if(fecha.length==8){
            var d = fecha.substr(6,2);
            var m = fecha.substr(4,2);
            var a =  fecha.substr(0,4);
        }
        switch(formatOut) {
            case 'sql':
                var fch= a+'-'+m+'-'+d;
                break;
            case 'print':
                var fch= d+'/'+m+'/'+a;
                break;
            case 'md':
                var fch = m+d;
                break;
            case 'number':
                var fch = a+m+d;
                break;
            case 'day':
                var fch = d;
                break;
            case 'month':
                var fch = m;
                break;
            case 'year':
                var fch = a;
                break;
            default:
               var fch = new Date(a, m-1,d);
        }
    
        return (fch); 
     }
}


